    ];

    var jssor_1_options = {
      $AutoPlay: 1,
      $SlideshowOptions: {
        $Class: $JssorSlideshowRunner$,
        $Transitions: jssor_1_SlideshowTransitions,
        $TransitionsOrder: 1
      },
      $ArrowNavigatorOptions: {
        $Class: $JssorArrowNavigator$
      },
      $ThumbnailNavigatorOptions: {
        $Class: $JssorThumbnailNavigator$,
        $SpacingX: 5,
        $SpacingY: 5
      }
    };

    var jssor_1_slider = new $JssorSlider$("jssor_1", jssor_1_options);

    /*#region responsive code begin*/

    var MAX_WIDTH = 980;

    function ScaleSlider() {
        var containerElement = jssor_1_slider.$Elmt.parentNode;
        var containerWidth = containerElement.clientWidth;

        if (containerWidth) {

            var expectedWidth = Math.min(MAX_WIDTH || containerWidth, containerWidth);

            jssor_1_slider.$ScaleWidth(expectedWidth);
        }
        else {
            window.setTimeout(ScaleSlider, 30);
        }
    }

    ScaleSlider();

    $Jssor$.$AddEvent(window, "load", ScaleSlider);
    $Jssor$.$AddEvent(window, "resize", ScaleSlider);
    $Jssor$.$AddEvent(window, "orientationchange", ScaleSlider);
    /*#endregion responsive code end*/
};

form.script:
$(function () {
    $('#datetimepicker1').datetimepicker();
});
$(document).ready(function(){
    $(".send-enq-as-lead").on('click', function(){
        var flag = 0;
        if($('#firstname').val()){
            $('#name-warn').addClass('hidden')
        }
        else{
            $('#name-warn').removeClass('hidden')   
            flag = 1;
        }
        if($('#email').val()){
            $('#email-warn').addClass('hidden')
        }
        else{
            $('#email-warn').removeClass('hidden')   
            flag = 1;
        }
        if($('#phone').val()){
            $('#phone-warn').addClass('hidden')
        }
        else{
            $('#phone-warn').removeClass('hidden')   
            flag = 1;
        }
        if($('#datetimeast').val()){
            $('#date-warn').addClass('hidden')
        }
        else{
            $('#date-warn').removeClass('hidden')   
            flag = 1;
        }
        if(flag == 0){
            $("#wait").css("display", "block");
            $.ajax({
                url: "/send/enquiry",
                type: "post",
                data: {'firstname':$('#firstname').val(),
                       'phone':$('#phone').val(),
                       'email':$('#email').val(),
                       'datetimeast':$('#datetimeast').val(),
                       'comments':$('#comments').val(),
                       'listing':$('#listing').val()},
                cache: false,
                dataType:"json",
                success: function(response){
                    console.log(response);
                    $("#wait").css("display", "none");
                    if(response['success'] == 'invalid-mail'){
                        $('#email-warn').removeClass('hidden')               
                    }
                    if(response['success'] == 'invalid-phone'){
                        $('#phone-warn').removeClass('hidden')               
                    }
                    if(response['success'] == 'lead'){
                        $("#close-enq-modal").trigger('click');
                        swal("Thank you for choosing Asteco.", "Enquiry created successfully with reference "+response['lead'], "success");
                    }
                    else if(response['success'] == 'lead exist'){
                        $("#close-enq-modal").trigger('click');
                        swal("Enquiry already created for this listing.\nReference : "+response['lead']);
                    }
                },
                error:function(a,b,c){
                    console.log(a);
                    console.log(b);
                    console.log(c);
                },  
            });
        }
    });
    $('#datetimeast').datepicker({
        format: "dd/mm/yyyy",
        autoclose: true
    });

    var listing_id = $("#listing-id").text();
    var live_loc = getLocationURL();
    function getLocationURL() {
        $.ajax({
            url: "/get/live/location",
            type: "post",
            data: {'listing_id':listing_id},
            cache: false,
            dataType:"json",
            success: function(response){
                document.getElementById('frame-geo-tag').src = response['success'];
            },
            error:function(a,b,c){
                console.log(a);
                console.log(b);
                console.log(c);
            },
        });
    };
});
